import axios from "axios";
import { completionPayload, idParamType, ParamType } from "features/todo/reducer/taskSlice";
const SERVER = 'http://127.0.0.1:8000/api/event'
// const SERVER = 'http://192.168.0.70:8000/api/event'
const headers = {
    'Content-Type': 'application/json',
    'Authorization': 'JWT fefege..'
}


function taskAPI( data: ParamType){
  console.log(data.date)
  return axios.get(`${SERVER}/date/${data.date}`)
}

function completeAPI(data : completionPayload, param:idParamType){
  console.log('taskComplete API 실행')
  return axios.post(`${SERVER}/complete/${param.id}`, JSON.stringify(data), { headers });
}


export default {
  taskAPI,
  completeAPI
}
