import PropTypes from "prop-types";
import React ,{ useEffect, useState } from "react";
import { suggestionResultRequest } from "features/suggestion/reducer/suggestionSlice";
import { useDispatch, useSelector } from 'react-redux'
import { Controller, useForm } from 'react-hook-form';

const SuggestionOneSingle = ({ data }) => {

// const [result, setResult] = useState({ user_id: '', contents: '', type: ''})

// function onSubmit() {
//     reset(result);
// }

// function handleSubmit(e) {
//   e.preventDefault();
//   console.log('You clicked submit.');
// }

// const handleSubmit = useForm({
//   mode: 'onChange',
//   defaultValues,
// });



// console.log(`입력된 데이터 ::: ${JSON.stringify(data)}`)

const [counter, setCounter] = useState(0)
const [suggestion, setSuggestion] = useState([])

const suggestionData = useSelector(state => state.suggestion.suggestionData)
if (suggestionData != null && counter < 1) {
  setCounter(counter + 1)
  setSuggestion(suggestionData['data'])
}
// console.log(`만족도 state ::: ${JSON.stringify(suggestionData)}`)
// console.log(`저장된 state ::: ${JSON.stringify(suggestion)}`)

function sayHello(e) {
  // e.preventDefault();
  console.log(JSON.stringify(e));
  console.log('일단 클릭 이벤트')
  console.log(suggestion[suggestion.length - 1].suggestion_id)
}

  return (
    <div>
      <div
        className="team-wrapper">
        <div className="team-img">
          <img
            className="img-fluid"
          />
          <div className="team-action">
          <h4>할까 말까</h4>  
            <a
              className="check"
              // onSubmit= {handleSubmit(async (data) => { await dispatch(suggestionResultRequest({
              //   ...data,
              //  })) })}
              onClick = {async data => await sayHello(data)}
              //  onClick = {console.log(window.localStorage.getItem)}
              // onChange={handleSubmit}
            >
              <i className="fa fa-check" />
            </a>
            <a
              className="remove"
              href={data.twitterLink}
            >
              <i className="fa fa-remove" />
            </a>
          </div>
        </div>
        <div className="team-content">
          <h4>{data.contents}</h4>
          <span>{`${data.start == null ? '' : data.start} `}{data.end == null ? '' : '~'}{data.end}</span>
          <span>{data.routine == null ? '' : `${data.routine}`}</span>
        </div>
      </div>
    </div>
  );
};

SuggestionOneSingle.propTypes = {
  data: PropTypes.object,
};

export default SuggestionOneSingle;
