import React, { useState } from "react";
import TextField from '@mui/material/TextField';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DateTimePicker from '@mui/lab/DateTimePicker';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Button from '@mui/material/Button';
import { Controller, useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux'
import dayjs from "dayjs";
import { addTaskRequest, taskRequest } from "../reducer/taskSlice";




export default function TaskAdd() {

    const dispatch = useDispatch()
    const inputValue = document.getElementById('new-todo-input').value

    return (<>
        <form>
            {/* <h2 className="label-wrapper">
            <label htmlFor="new-todo-input" className="label__lg">
            What needs to be done?
            </label>
            </h2> */}
            <input
            type="text"
            id="new-todo-input"
            className="input input__lg"
            name="text"
            autoComplete="off"
            placeholder="What needs to be done?"
            />
            <button style={{marginLeft:"20px"}} type="submit" className="btn btn__primary btn__lg" 
            onSubmit={dispatch(addTaskRequest({title: document.getElementById('new-todo-input').value}))}
            >
                Add
            </button>
        </form>   
    </>)
}
